mapper.update:
update loan_repayment_payment_record
set request     = #{request},
    response=#{response},
    update_time = #{updateTime}
WHERE id = #{id}

update loan_repayment_payment_record
set status      = #{status},
    update_time = #{updateTime}
WHERE id = #{id}

update loan_repayment_payment_record
set search_request = #{request},
    search_response=#{response},
    update_time    =
        #{updateTime}
WHERE id = #{id}

update loan_repayment_payment_record
set business_id = #{businessId},
    update_time = #{updateTime}
WHERE id =
      #{id}

update loan_repayment_payment_record
set actual_amount = #{actualAmount},
    update_time   = #{updateTime}
WHERE id =
      #{id}

update loan_repayment_payment_record
set clabe       = #{clabe},
    bar_code    = #{barCode},
    update_time = #{updateTime}
WHERE id = #{id}

update loan_repayment_payment_record
set pay_fee     = #{payFee},
    update_time = #{updateTime}
WHERE id = #{id}

mapper.select:
SELECT *
FROM loan_repayment_payment_record
WHERE order_id = #{orderId}
  AND status = #{status}

SELECT *
FROM loan_repayment_payment_record
WHERE status = #{status}
  AND create_time > #{startTime}
  AND create_time < #{endTime}
